import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [incidentData, setIncidentData] = useState('');
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setReport(null);

    try {
      const parsedData = JSON.parse(incidentData);
      const response = await axios.post('http://10.99.8.8:3002/analyse', parsedData);
      setReport(response.data);
    } catch (err) {
      setError(err.message.includes('JSON') ? 'Invalid JSON format' : 'Analysis failed');
    } finally {
      setLoading(false);
    }
  };

  const getSeverityClass = (level) => {
    const severity = level?.toLowerCase();
    if (severity === 'critical') return 'severity-critical';
    if (severity === 'high') return 'severity-high';
    if (severity === 'medium') return 'severity-medium';
    return 'severity-low';
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>SOC Incident Analyzer</h1>
        <p>Security Operations Center - Automated Incident Analysis</p>
      </header>

      <main className="app-main">
        <section className="input-section">
          <h2>Incident Details</h2>
          <form onSubmit={handleSubmit}>
            <textarea
              value={incidentData}
              onChange={(e) => setIncidentData(e.target.value)}
              placeholder="Paste incident details in JSON format..."
              rows={10}
              required
            />
            <button type="submit" disabled={loading || !incidentData}>
              {loading ? 'Analyzing...' : 'Generate RCA'}
            </button>
          </form>
          {error && <div className="error">{error}</div>}
        </section>

        {report && (
          <section className="report-section">
            <h2>Security Incident Report</h2>

            <div className="report-content">
              <div className="report-block">
                <h3>Executive Summary</h3>
                <p>{report.executiveSummary}</p>
              </div>

              <div className="report-block">
                <h3>Severity Assessment</h3>
                <div className={`severity-badge ${getSeverityClass(report.severityAssessment?.level)}`}>
                  {report.severityAssessment?.level || 'Unknown'}
                </div>
                <p>{report.severityAssessment?.justification}</p>
              </div>

              <div className="report-block">
                <h3>Incident Details</h3>
                {typeof report.incidentDetails === 'object' ? (
                  <div>
                    {report.incidentDetails.id && (
                      <p><strong>Incident ID:</strong> {report.incidentDetails.id}</p>
                    )}
                    {report.incidentDetails.typeOfIncident && (
                      <div>
                        <h4>Type of Incident</h4>
                        <p>{report.incidentDetails.typeOfIncident}</p>
                      </div>
                    )}
                    {Object.entries(report.incidentDetails).filter(([key]) => !['id', 'typeOfIncident'].includes(key)).length > 0 && (
                      <pre>{JSON.stringify(
                        Object.entries(report.incidentDetails)
                          .filter(([key]) => !['id', 'typeOfIncident'].includes(key))
                          .reduce((obj, [key, value]) => ({...obj, [key]: value}), {}), 
                        null, 
                        2
                      )}</pre>
                    )}
                  </div>
                ) : (
                  <p>{report.incidentDetails}</p>
                )}
              </div>

              <div className="report-block">
                <h3>Root Cause Analysis</h3>
                <div className="analysis-content">
                  {typeof report.rootCauseAnalysis === 'object' ? (
                    <div>
                      {report.rootCauseAnalysis.primaryRootCause && (
                        <>
                          <h4>Primary Root Cause</h4>
                          <p>{report.rootCauseAnalysis.primaryRootCause}</p>
                        </>
                      )}

                      {report.rootCauseAnalysis.technicalAnalysis && (
                        <>
                          <h4>Technical Analysis</h4>
                          <p>{report.rootCauseAnalysis.technicalAnalysis}</p>
                        </>
                      )}

                      {report.rootCauseAnalysis.attackVector && (
                        <>
                          <h4>Attack Vector</h4>
                          <p>{report.rootCauseAnalysis.attackVector}</p>
                        </>
                      )}

                      {report.rootCauseAnalysis.contributingFactors && (
                        <>
                          <h4>Contributing Factors</h4>
                          <ul>
                            {Array.isArray(report.rootCauseAnalysis.contributingFactors) ? 
                              report.rootCauseAnalysis.contributingFactors.map((factor, idx) => (
                                <li key={idx}>{typeof factor === 'object' ? `${factor.type}: ${factor.description}` : factor}</li>
                              )) :
                              <li>{report.rootCauseAnalysis.contributingFactors}</li>
                            }
                          </ul>
                        </>
                      )}

                      {report.rootCauseAnalysis.controlFailureAnalysis && (
                        <>
                          <h4>Control Failure Analysis</h4>
                          {typeof report.rootCauseAnalysis.controlFailureAnalysis === 'object' ? (
                            <div>
                              {Object.entries(report.rootCauseAnalysis.controlFailureAnalysis).map(([key, value]) => (
                                <div key={key}>
                                  <strong>{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}:</strong>
                                  <p>{value}</p>
                                </div>
                              ))}
                            </div>
                          ) : (
                            <p>{report.rootCauseAnalysis.controlFailureAnalysis}</p>
                          )}
                        </>
                      )}
                    </div>
                  ) : (
                    <p>{report.rootCauseAnalysis}</p>
                  )}
                </div>
              </div>

              <div className="report-block">
                <h3>Impact Assessment</h3>
                {typeof report.impactAssessment === 'object' ? (
                  <div>
                    {Object.entries(report.impactAssessment).map(([key, value]) => (
                      <div key={key} className="impact-item">
                        <h4>{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</h4>
                        <p>{value}</p>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p>{report.impactAssessment}</p>
                )}
              </div>

              <div className="report-block">
                <h3>Recommended Actions</h3>
                <div className="actions-grid">
                  <div>
                    <h4>Immediate Actions</h4>
                    <ul>
                      {report.recommendedActions?.immediate?.map((action, idx) => (
                        <li key={idx}>{action}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h4>Long-term Remediation</h4>
                    <ul>
                      {report.recommendedActions?.longTerm?.map((action, idx) => (
                        <li key={idx}>{action}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>

              <div className="report-block">
                <h3>Prevention Measures</h3>
                {Array.isArray(report.preventionMeasures) ? (
                  <ul>
                    {report.preventionMeasures.map((measure, idx) => (
                      <li key={idx}>{measure}</li>
                    ))}
                  </ul>
                ) : (
                  <p>{report.preventionMeasures}</p>
                )}
              </div>
            </div>
          </section>
        )}
      </main>
    </div>
  );
}

export default App;
